$(document).ready(function(){$('.downloable_list').slick({fade:!0,swipe:!1,arrows:!1,adaptiveHeight:!0});$('.kit_slider').slick({fade:!0,swipe:!1,arrows:!1});$('.brand_list li').click(function(){var kitTarget=$(this).data('kit-item');$('.brand_list li').removeClass('active');$(this).addClass('active');$('.kit_slider').slick('goTo',kitTarget)});$('.year_list li').click(function(){var yearTarget=$(this).data('year-item');$('.year_list li').removeClass('active');$(this).addClass('active');$('.downloable_list').slick('goTo',yearTarget)})})